import React, { Component } from "react";
import v4 from "uuid";

class Playlist extends Component {
  constructor(props) {
    super();
    const { items, match, token } = props;
    let item;
    item = items.filter(item => match.params.playlistId === item.id)[0];
    this.state = {
      tracks: null,
      playlistId: match.params.playlistId,
      item,
      token
    };
  }
  componentDidMount() {
    this.getPlaylist();
  }
  getPlaylist = () => {
    const { token, playlistId } = this.state;
    fetch(`https://api.spotify.com/v1/playlists/${playlistId}`, {
      method: "GET",
      headers: {
        Authorization: "Bearer " + token
      }
    })
      .then(res => res.json())
      .then(response => {
        console.log(response);
        this.setState({
          tracks: response.tracks.items
        });
      })
      .catch(error => console.error("Error:", error));
  };

  deleteTrack = e => {
    let id = e.target.id;
    console.log(e.target);
    let index = e.target.index;
    const { token, playlistId } = this.state;
    fetch(`https://api.spotify.com/v1/playlists/${playlistId}/tracks`, {
      method: "DELETE",
      headers: {
        Authorization: "Bearer " + token
      },
      body: JSON.stringify({
        tracks: [
          {
            uri: `spotify:track:${id}`,
            positions: [index]
          }
        ]
      })
    })
      .then(res => res.json())
      .then(response => {
        console.log(response);
      })
      .catch(error => console.error("Error:", error));
  };

  render() {
    const { item, tracks } = this.state;
    return (
      <div>
        {item && (
          <div>
            <h1>{item.name}</h1>
            <img src={item.images[0].url} height="200" />
            <ul>
              {tracks &&
                tracks.map((track, idx) => (
                  <li key={v4()}>
                    {track.track.name} - {track.track.artists[0].name}
                    <button
                      id={track.track.id}
                      value={idx}
                      onClick={this.deleteTrack}
                    >
                      -
                    </button>
                  </li>
                ))}
            </ul>
          </div>
        )}
      </div>
    );
  }
}

export default Playlist;
